$db->do("create table IF NOT EXISTS message_detail (event_time text PRIMARY KEY, name text, length integer, smiles integer, changed integer, fucks integer, qs integer, links integer, del integer);"); # —оздаем новую таблицу в базе данных

#-----------------/db--------------------------------
while (<LOG>) 
{ 
  chop;
  if (/^\[([0-9]{2}.[0-9]{2}.[0-9]{4}.*?[0-9]{2}:[0-9]{2}:[0-9]{2})\](?! \*\*\*)/ or /^\[([0-9]{2}.[0-9]{2}.[0-9]{4}.*?[0-9]:[0-9]{2}:[0-9]{2})\](?! \*\*\*)/)
  {
    if ($tBlock[0] && ($tBlock[0] =~/^([0-9]{2}:[0-9]{2}:[0-9]{2}).*?/ or /^([0-9]:[0-9]{2}:[0-9]{2}).*?/)){next;}#цитата, ее не считаем.
    else
    {
      if (!$tBlock[0])
			{
				$tBlock[0] = $1;
				$1 =~ /(\d+.\d+.\d+).*?(\d+:\d+:\d+)/;
				$sqlstring{event_time} = $1." ".$2;
			}#первое сообщение 
      $mCount++;
      $tBlock[1] = $1;
      if (&Compare (@tBlock,1)) #нормальное сообщение
      {
        /(\d+.\d+.\d+).*?(\d+:\d+:\d+)/;
				$sqlstring{event_time} = $1." ".$2;
				$'=~/ (.*?):/;
        if (!($1 ~~ @names)){push (@names,$1);}#если нет такого имени - добавл€ем
      }
      else {next;} #цитата
      $tBlock[0] = $tBlock[1];
    }
  }
  elsif (/^\[([0-9]{2}:[0-9]{2}:[0-9]{2}).*?\](?! \*\*\*)/ or /^\[([0-9]:[0-9]{2}:[0-9]{2}).*?\](?! \*\*\*)/)
  {
    if ($tBlock[0] && ($tBlock[0] =~/^([0-9]{2}.[0-9]{2}.[0-9]{4}.*?[0-9]{2}:[0-9]{2}:[0-9]{2})/ or /^([0-9]{2}.[0-9]{2}.[0-9]{4}.*?[0-9]:[0-9]{2}:[0-9]{2})/)){next;}#цитата, ее не считаем.
    else 
    {
      if (!$tBlock[0])
			{
				$tBlock[0] = $1;
				$1 =~ /(\d+:\d+:\d+)/;
				$sqlstring{event_time} = $1." ".$2;
			}#первое сообщение 
