	# print $sqlstring{event_time}."\n";
  foreach my $name (@names)
  {
    if (/\]\s$name/)
    {
      $stat{$name}{total}++;
      if (&Check('smiles',$_)){ $stat{$name}{smiles}++; }
      if (/\| »зменены/){ $stat{$name}{changed}++; }
      if (&Check('fucks',$_)){ $stat{$name}{fucks}++; }
      if (/\?/){ $stat{$name}{qs}++; }
      if (/—ообщение удалено/){ $stat{$name}{del}++; }
      if (&Check('links',$_)){ $stat{$name}{links}++; }
    }
  }    
}
