#----------------------------subs------------------------------------
sub Compare 
{
	if ($_[2]=="1")
	{
		my ($dd, $mm, $yyyy, $hh, $mi, $ss) = ($_[0] =~ /(\d+).(\d+).(\d+).*?(\d+):(\d+):(\d+)/);# [18.08.2012 15:30:35]
		my $first = timelocal($ss, $mi, $hh, $dd, $mm, $yyyy);
		($dd, $mm, $yyyy, $hh, $mi, $ss) = ($_[1] =~ /(\d+).(\d+).(\d+).*?(\d+):(\d+):(\d+)/);
		my $second = timelocal($ss, $mi, $hh, $dd, $mm, $yyyy);
		if ($second>$first) {return 1;}
		else {return 0;}
	}
	else
	{
		my ($hh, $mi, $ss) = ($_[0] =~ /(\d+):(\d+):(\d+).*?/);#[19:26:42]
		my $first = timelocal($ss, $mi, $hh);
		($hh, $mi, $ss) = ($_[1] =~ /(\d+):(\d+):(\d+).*?/);
		my $second = timelocal($ss, $mi, $hh);
		if ($second>$first) {return 1;}
		else {return 0;}
	}
}

sub CreateTotal{
  my $i=2;
  foreach my $type (@types)
  {
    $workbook -> Worksheets(1) -> Range("A".$i++)-> {'Value'} = sprintf($type);
  }
  $workbook -> Worksheets(1) -> {Name} = "Total";
  $i=0;
  foreach my $name (@names)
  {
    my $y=2;
    foreach my $type (@types)
    {
			if ($stat{$name}{$type})
			{
				$workbook -> Worksheets(1) -> Range($letters[$i].$y++)-> {'Value'} = sprintf("$stat{$name}{$type}");
			}
			else
			{
				$workbook -> Worksheets(1) -> Range($letters[$i].$y++)-> {'Value'} = sprintf("0");
			}
		}
    $i++;
  }
}

sub CreateTop{
  for(my $i=2;$i<8;$i++)#----------------------------------------------------------исправить
  {
    $workbook -> Worksheets($i) -> {Name} = "$names[$i-2]";
    $workbook -> Worksheets($i) -> Range("A1")-> {'Value'} = sprintf("Date");
    for(my $y=0;$y<@types;$y++)
    {
      $workbook -> Worksheets($i) -> Range($letters[$y]."1")-> {'Value'} = sprintf($types[$y]);
    }
  }
}
sub CreateExcel {
  $excel = CreateObject OLE 'Excel.Application' or die $!; # открываем Excel
  $workbook = $excel -> Workbooks -> Add(); # создаем новый документ
}


